#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\omkar\Documents\Blog\node_modules\.pnpm\yaml-language-server@1.15.0\node_modules\yaml-language-server\bin\node_modules;C:\Users\omkar\Documents\Blog\node_modules\.pnpm\yaml-language-server@1.15.0\node_modules\yaml-language-server\node_modules;C:\Users\omkar\Documents\Blog\node_modules\.pnpm\yaml-language-server@1.15.0\node_modules;C:\Users\omkar\Documents\Blog\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/proc/cygdrive/c/Users/omkar/Documents/Blog/node_modules/.pnpm/yaml-language-server@1.15.0/node_modules/yaml-language-server/bin/node_modules:/proc/cygdrive/c/Users/omkar/Documents/Blog/node_modules/.pnpm/yaml-language-server@1.15.0/node_modules/yaml-language-server/node_modules:/proc/cygdrive/c/Users/omkar/Documents/Blog/node_modules/.pnpm/yaml-language-server@1.15.0/node_modules:/proc/cygdrive/c/Users/omkar/Documents/Blog/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../bin/yaml-language-server" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../bin/yaml-language-server" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../bin/yaml-language-server" $args
  } else {
    & "node$exe"  "$basedir/../../bin/yaml-language-server" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
